import React, { useContext } from 'react';
import './CartItems.css';
import { ShopContext } from '../../Context/ShopContext';
import remove_icon from '../Assets/cart_cross_icon.png';

const CartItems = () => {
  const { all_product, cartItems, removeFromCart } = useContext(ShopContext);

  // Filter out items not in the cart
  const cartProducts = all_product.filter((e) => cartItems[e.id] > 0);

  return (
    <div className='cartitems'>
      <div className="cartitems-formart-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {cartProducts.length > 0 ? (
        cartProducts.map((e) => (
          <div key={e.id}>
            <div className="cartitems-format">
              <img src={e.image} alt="Product" className='carticon-product-icon' />
              <p>{e.name}</p>
              <p>Ksh {e.new_price}</p>
              <p className='cartitems-quantity'>{cartItems[e.id]}</p>
              <p>Ksh {e.new_price * cartItems[e.id]}</p>
              <img
                src={remove_icon}
                alt="Remove"
                onClick={() => removeFromCart(e.id)}
                className="cart-remove-icon"
              />
            </div>
            <hr />
          </div>
        ))
      ) : (
        <p>Your cart is empty</p>
      )}
    </div>
  );
};

export default CartItems;
