import React from 'react';
import './Breadcrum.css';
import { Link, useLocation } from 'react-router-dom'; // Import Link and useLocation
import arrow_icon from '../Assets/breadcrum_arrow.png';

const Breadcrum = (props) => {
    const { product } = props;
    const location = useLocation();

    // Helper to capitalize strings for display
    const capitalize = (str) => {
        if (!str) return '';
        return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
    };

    // If product is NOT provided, generate breadcrumbs from the current path
    if (!product || !product.category) { // Also check if product.category is missing
        const pathnames = location.pathname.split('/').filter(x => x); // e.g., ['', 'mens', 'shirt'] -> ['mens', 'shirt']

        // Always start with HOME
        let breadcrumbPathElements = [<Link key="home" to="/">HOME</Link>];

        if (pathnames.length === 0 && location.pathname === '/') {
            // It's just the homepage, breadcrumbPathElements already has HOME
        } else if (pathnames.length > 0) {
            pathnames.forEach((name, index) => {
                const routeTo = `/${pathnames.slice(0, index + 1).join('/')}`;
                // Replace hyphens with spaces and capitalize each word for display
                const displayName = name.replace(/-/g, ' ').split(' ').map(capitalize).join(' ');

                breadcrumbPathElements.push(<img key={`arrow-${index}`} src={arrow_icon} alt="arrow" />);
                if (index === pathnames.length - 1) { // Last item in path is not a link
                    breadcrumbPathElements.push(<span key={name}>{displayName.toUpperCase()}</span>);
                } else {
                    breadcrumbPathElements.push(<Link key={name} to={routeTo}>{displayName.toUpperCase()}</Link>);
                }
            });
        } else {
            // Fallback for unusual paths or if you prefer to hide breadcrumbs on certain non-product pages
            return <div className='breadcrum'></div>; // Or return null to render nothing
        }

        return <div className='breadcrum'>{breadcrumbPathElements}</div>;
    }

    // Product is defined, render the detailed product-specific breadcrumb
    return (
        <div className='breadcrum'>
            <Link to="/">HOME</Link> <img src={arrow_icon} alt="arrow" />
            <Link to="/shop">SHOP</Link> <img src={arrow_icon} alt="arrow" /> {/* Assuming /shop is your main shop page */}
            {/* Assuming your category routes are like /mens, /womens etc. */}
            <Link to={`/${product.category.toLowerCase()}`}>{product.category.toUpperCase()}</Link>
            <img src={arrow_icon} alt="arrow" /> {product.name.toUpperCase()}
        </div>
    );
};

export default Breadcrum;
