import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For redirecting after login
import { doSignInWithEmailAndPassword } from '../firebase/auth'; // Adjust path if needed
// import { useAuth } from '../Context/authContext'; // You might use this later
import './CSS/LoginSignup.css'

const LoginSignup = () => {
  const navigate = useNavigate();
  // const { setCurrentUser } = useAuth(); // Example if you want to set user in context directly, though onAuthStateChanged usually handles this

  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [isLoggingIn, setIsLoggingIn] = useState(false); // To disable button during login

  const handleLogin = async (e) => {
    e.preventDefault();
    if (isLoggingIn) return;

    setIsLoggingIn(true);
    setError('');

    try {
      await doSignInWithEmailAndPassword(email, password);
      // Firebase's onAuthStateChanged will handle setting the currentUser in your AuthContext
      // Redirect to home page or dashboard after successful login
      navigate('/'); // Or your desired route
    } catch (err) {
      setError(err.message);
      setIsLoggingIn(false);
    }
  };

  return (
    <div className='loginsignup'>
      <div className="loginsignup-container">
        <h1>Log In</h1>
        <form onSubmit={handleLogin} className="loginsignup-fields">
          <input 
            type="email" 
            placeholder='Your Email Address' 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required 
          />
          <input 
            type="password" // Changed type to password
            placeholder='YourPassword' 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          {error && <p className="loginsignup-error">{error}</p>}
          <button type="submit" disabled={isLoggingIn}>
            {isLoggingIn ? 'Logging In...' : 'Login'}
          </button>
        </form>
        <p className="loginsignup-login">Don't have an account? <span onClick={() => {/* TODO: Navigate to Signup or toggle view */ alert("Navigate to Sign Up page/view");}} style={{cursor: 'pointer', color: '#ff4141', fontWeight: '600'}}>Sign Up here</span></p>
        <div className="loginsignup-agree">
          <input type="checkbox" name='' id='' />
          <p>By continuing, I agree to the terms of use & privacy policy.</p>
        </div>
      </div>
    </div>
  )
}

export default LoginSignup
